mapper.select:
select
	t.fileId,
	t.fileName,
	t.fileType,
	t.fileSize,
    t.filePtah,
	t.uploadTime,
	t.editTime,
	t.attrUser,
	t.categoryId,
	t.downloadTimes,
	t.state,
	t.remark
from filesInfo t


		and t.fileId = #{fileId}


		and t.fileName = #{fileName}


		and t.fileType = #{fileType}


		and str_to_date(t.uploadTime, '%Y-%m-%d')  >=  #{uploadTimeStart}


		and str_to_date(t.uploadTime, '%Y-%m-%d')  <=  #{uploadTimeEnd}


		and str_to_date(t.editTime, '%Y-%m-%d')  >=  #{editTimeStart}


		and str_to_date(t.editTime, '%Y-%m-%d')  <=  #{editTimeEnd}


		and t.attrUser = #{attrUser}


		and t.categoryId = #{categoryId}


		and t.state = #{state}


order by t.uploadTime, t.editTime

mapper.update:
update filesInfo t
set t.downloadTimes = t.downloadTimes + 1
where t.fileId = #{fileId}

update filesInfo t
set t.state = #{state}
where t.fileId in

	#{item}
